                    : 'slide-in-from-right';

      $this.removeClass(classList).addClass(className);      
    })
    .on('mouseleave', 'li', function(e) {
      var $this = $(this);
      var width = $this.outerWidth();
      var xx = e.offsetX;
      var className = (xx < (width / 2))
                    ? 'slide-out-from-left'
                    : 'slide-out-from-right';
      $this.removeClass(classList).addClass(className);
    });

});
```

